
$(document).ready(function() {
    filterData(orderListUrl, "order-table");
});

function submitData() {
    filters.admin_order_status = $('#admin_order_status').val();
    filters.admin_order_id = $('#admin_order_id').val();
    filters.admin_product_name = $('#admin_product_name').val();
    filterData(orderListUrl, "order-table");
}

function clear_search_val() {
    $('#search-order-form').trigger('reset');
    $('#admin_order_status').val('');
    $('#admin_order_id').val('');
    $('#admin_product_name').val('');
    filters.admin_order_status = '';
    filters.admin_order_id = '';
    filters.admin_product_name = '';
    filters.order_from_date = '';
    filters.order_to_date = '';
    filterData(orderListUrl, "order-table");
}