    $('#subscribe-us-form').ajaxForm(function(res) {
      $('#subscribe-us-spinner').hide();
      Toast(res.msg, 3000, res.flag);
      if (res.flag == 1) {
        $('#subscribe-us-form ')[0].reset();
      }
      $('#subscribe-us-btn').prop('disabled', false);
    }).submit();
  });


  // ===== Scroll to Top ==== 
  $(window).scroll(function() {
    if ($(this).scrollTop() >= 50) { // If page is scrolled more than 50px
      $('#return-to-top').fadeIn(200); // Fade in the arrow
    } else {
      $('#return-to-top').fadeOut(200); // Else fade out the arrow
    }
  });
  $('#return-to-top').click(function() { // When arrow is clicked
    $('body,html').animate({
      scrollTop: 0 // Scroll to top of body
    }, 500);
  });

  refreshCartLogo();

  function refreshCartLogo() {

    let cart = JSON.parse(localStorage.getItem('cart')) || {};
    if (Object.keys(cart).length > 0) {
      $('#cart-page-button').removeClass('d-none');
      $('#cart-quantity').text(Object.keys(cart).length);
    } else {
      $('#cart-page-button').addClass('d-none');
      $('#cart-quantity').text(0);
    }
  }
